item.description:
docker exec -i mariadb mysql -uroot -p -e &quot;GRANT ALL PRIVILEGES ON jira.* TO 'jira'@'%' IDENTIFIED BY 'jira2017'; FLUSH PRIVILEGES;&quot;
docker exec -i mariadb mysql -ujira -pjira2017 -e &quot;CREATE DATABASE IF NOT EXISTS jira DEFAULT CHARSET utf8 COLLATE utf8_general_ci;&quot;
docker run -d --restart=always --name jira -p 8080:8080 rinetd/jira
